import React, { useState } from "react";
import { Card, Button, Select, Form, Input, DatePicker, message } from "antd";
import { spaces } from "../data/spaces";

const { Option } = Select;

const Booking = () => {
  const [selectedSpace, setSelectedSpace] = useState<number | null>(null);

  const onFinish = (values: any) => {
    message.success(`Booking confirmed for ${values.name}!`);
  };

  return (
    <div style={{ maxWidth: 600, margin: "auto", padding: "20px" }}>
      <h1>Book Your Space</h1>

      <Select
        style={{ width: "100%", marginBottom: 20 }}
        placeholder="Select a co-working space"
        onChange={(value) => setSelectedSpace(value)}
      >
        {spaces.map((space) => (
          <Option key={space.id} value={space.id}>
            {space.name} - {space.price}
          </Option>
        ))}
      </Select>

      {selectedSpace && (
        <Card cover={<img alt="Selected Space" src={spaces.find((s) => s.id === selectedSpace)?.image} />}>
          <h3>{spaces.find((s) => s.id === selectedSpace)?.name}</h3>
          <p>Price: {spaces.find((s) => s.id === selectedSpace)?.price}</p>
        </Card>
      )}

      <Form layout="vertical" onFinish={onFinish}>
        <Form.Item
          label="Your Name"
          name="name"
          rules={[{ required: true, message: "Please enter your name" }]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label="Email"
          name="email"
          rules={[
            { required: true, message: "Please enter your email" },
            { type: "email", message: "Please enter a valid email" },
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label="Booking Date"
          name="date"
          rules={[{ required: true, message: "Please select a date" }]}
        >
          <DatePicker style={{ width: "100%" }} />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" block>
            Confirm Booking
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default Booking;
