import logo from "./logo.svg";
import "./App.css";
import { MdCurrencyExchange } from "react-icons/md";
import { useState } from "react";

function App() {
  const [amount, setAmount] = useState(1);
  const [fromcurrency, setFromCurrency] = useState("USD");
  const [tocurrency, setToCurrency] = useState("INR");
  const [exchangeRate, setExchangeRate] = useState(); // 1 USD = 82 INR
  return (
    <div className="App">
      <div className="logo">
        <MdCurrencyExchange />
      </div>

      <div className="data">
        <h2>Currency Converter</h2>
        <div className="input">
          <div className="amount">
            <label htmlFor="amount">Amount</label>
            <input type="number" id="amount" className="textbox" value={amount}/>
          </div>
          <div className="from">
            <label htmlFor="from">From Currency</label>
            <select id="from">
              <option value="USD">{fromcurrency}</option>
            </select>
          </div>
          <div className="to">
            <label htmlFor="to">To Currency</label>
            <select id="to">
              <option value="INR">{tocurrency}</option>
            </select>
          </div>
          <p>{amount} {fromcurrency} is Equal to {exchangeRate} {tocurrency}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
