import React, { useState } from 'react';
import './App.css';

const Qrcode = () => {
  const [img, setImg] = useState('');
  const [loading, setLoading] = useState(false);
  const [qr, setQr] = useState('');

  const generate = () => {
    if (!qr.trim()) {
      alert("Please enter a value for QR code generation.");
      return;
    }
    setLoading(true);
    try {
      const URL = `https://api.qrserver.com/v1/create-qr-code/?size=200x200&data=${encodeURIComponent(qr)}`;
      setImg(URL);
    } catch (error) {
      console.log("Generation failed", error);
    } finally {
      setLoading(false);
      setQr(''); // Clear the input field after generation
    }
  };

  const download = () => {
    if (!img) return;
    const qrImage = new Image();
    qrImage.crossOrigin = "anonymous";
    qrImage.src = img;

    qrImage.onload = () => {
      const canvas = document.createElement('canvas');
      canvas.width = 200;
      canvas.height = 200;
      canvas.getContext('2d').drawImage(qrImage, 0, 0);
      const link = document.createElement('a');
      link.href = canvas.toDataURL('image/png');
      link.download = 'qrcode.png';
      link.click();
    };
  };

  return (
    <div className="app-container">
      <h1>QR CODE GENERATOR</h1>
      {loading && <p>Generating, please wait...</p>}
      {img && <p>QR code for "<strong>{img.split('data=')[1]}</strong>" generated successfully!</p>}
      {img && <img src={img} alt="QR code" />}
      <div>
        <label htmlFor="dataInput" className="inputlabel">URL or Any Data for QR code generation</label>
        <input
          type="text"
          value={qr}
          id="dataInput"
          onChange={(e) => setQr(e.target.value)}
        />
        <button className="generate_button" disabled={loading} onClick={generate}>Generate QR code</button>
        <button className="download_button" onClick={download}>Download QR code</button>
      </div>
      <p className="footer">Designed by <a href="https://resume-muthupandi-raj.web.app/">Muthupandi Raj</a></p>
    </div>
  );
};

export default Qrcode;
