import React, { useState } from 'react';
import './App.css';

const Qrcode = () => {
    const [qr, setQr] = useState('');
    const [size, setSize] = useState('');
    const [img, setImg] = useState('');
    const [loading, setLoading] = useState(false);

    const generateQrCode = async () => {
        setLoading(true);
        try {
            const URL =`https://api.qrserver.com/v1/create-qr-code/?size=${size}x${size}&data=${encodeURIComponent(qr)}`
            setImg(URL)
        } finally {
            setLoading(false);
        }
    }

    const download = () => {
        if (!img) return;
        const link = document.createElement('a');
        link.href = img;
        link.download = 'qrcode.png';
        link.click();
    }

    return (
        <div className="app-container">
            <h1>QR CODE GENERATOR</h1>
            {loading && <p>Generating Please Wait...</p>}
            {img && <img src={img} alt="Generated QR Code"/>}
            <div>
                <label htmlFor="dataInput" className="inputlabel">Data for QR code</label>
                <input type="text" value={qr} id="dataInput" onChange={(e) => setQr(e.target.value)}/>
                <label htmlFor="sizeInput" className="inputlabel">Image size</label>
                <input type="text" id="sizeInput" value={size} onChange={(e) => setSize(e.target.value)}/>
                <button className="generate_button" onClick={generateQrCode} disabled={loading}>Generate</button>
                {img && <button className="download_button" onClick={download}>Download</button>}
            </div>
        </div>
    );
}

export default Qrcode;
