import React, { useState } from 'react'
import './App.css';

const Qrcode = () => {
    const [img, setImg] = useState('');
    const [loading, setLoading] = useState(false);
    const [qr, setQr] = useState('')
    const [size, setSize] = useState('')

    const generate = (async) => {
        setLoading(true)
        try{
            const URL =`https://api.qrserver.com/v1/create-qr-code/?size=${size}x${size}&data=${encodeURIComponent(qr)}`
            setImg(URL)
        }
        catch(error){
            console.log("generation failed",error);
        }
        finally{
            setLoading(false)
        }
    }

  
    const download = () => {
        if (!img) return;
        const qrImage = new Image();
        qrImage.crossOrigin = "anonymous";
        qrImage.src = img;
      
        qrImage.onload = () => {
          const canvas = document.createElement('canvas');
          canvas.getContext('2d').drawImage(qrImage, 0, 0);
      
          const link = document.createElement('a');
          link.href = canvas.toDataURL('image/png');
          link.download = 'qrcode.png';
          link.click();
        };
      };
      
    

    
  return (
    <div className="app-container">
      <h1>QR CODE QENARETOR</h1>
      {loading && <p>Generating Please Wait...</p>}
      {img && <img src={img}/>}
      <div>
        <label htmlFor="dataInput" className="inputlabel">URL or Any Data for QR code genaration</label>
        <input type="text" value={qr} id="dataInput" onChange={(e) => setQr(e.target.value)}/>
        {/* <label htmlFor="sizeInput" className="inputlabel">Image size</label>
        <input type="text" id="sizeInput" value={size} onChange={(e) => setSize(e.target.value)}/> */}
        <button className="genarete_button" disabled={loading} onClick={generate}>Generate QR code</button>
        <button className="download_button" onClick={download}>Download QR code</button>
      </div>
      <p className="footer">Designed by <a href="https://resume-muthupandi-raj.web.app/">Muthupandi Raj</a></p>
    </div>
  )
}

export default Qrcode
