import React, { useState } from 'react'
import './App.css';

const Qrcode = () => {
    const [img, setImg] = useState('');
    const [loading, setLoading] = useState(false);

    const generate = (async) => {
        setLoading(true)
        try{
            const URL ="https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=muthupandi_raj"
            setImg(URL)
        }
        catch(error){
            console.log("generation failed",error);
        }
        finally{
            setLoading(false)
        }
        
    }
        // const data = document.getElementById('dataInput').value;
        // const size = document.getElementById('sizeInput').value;
        // setImg(`https://api.qrserver.com/v1/create-qr-code/?size=${size}&data=${data}`)

    const download = () => {
        // const link = document.createElement('a');
        // link.href = img;
        // link.download = 'qrcode.png';
        // document.body.appendChild(link);
        // link.click();
        // document.body.removeChild(link);
    }
  return (
    <div className="app-container">
      <h1>QR CODE QENARETOR</h1>
      {loading && <p>Generating Please Wait...</p>}
      {img && <img src={img}/>}
      <div>
        <label htmlFor="dataInput" className="inputlabel">Data for QR code</label>
        <input type="text" id="dataInput" />
        <label htmlFor="sizeInput" className="inputlabel">Image size</label>
        <input type="text" id="sizeInput" />
        <button className="genarete_button" onClick={generate}>Generate QR code</button>
        <button className="download_button" onClick={download}>Download QR code</button>
      </div>
      <p className="footer">Designed by <a href="https://resume-muthupandi-raj.web.app/">Muthupandi Raj</a></p>
    </div>
  )
}

export default Qrcode
